import React, { useState, useEffect } from "react";
import axios from "axios";
import { Column, Row } from "simple-flexbox";
import { createUseStyles } from "react-jss";
import TasksComponent from "./AgreementComponent";
import CreateButton from "../../components/CreateButton"; 
import SLUGS from "../../resources/slugs";
import { convertSlugUrl } from "../../resources/utils";
import { useHistory, useLocation } from "react-router-dom";

const useStyles = createUseStyles({
    buttonRow: {
    marginTop: 10,
    "@media (max-width: 768px)": {
      marginTop: 0,
    },
  },
  lastRow: {
    marginTop: 30,
  },
  tasks: {
    marginTop: 0,
    "@media (max-width: 1024px)": {
      marginTop: 30,
    },
  },
  buttonStyle: {
    display: "flex",
    justifyContent: "flex-end",
  },
});



function DashboardComponent() {

  const { push } = useHistory();
  let location = useLocation();
  const id = location.pathname.split("/")[2];
  const [items, setItems] = useState();
  
  useEffect(() => {
    getItems();
  }, []);

  const generateAgreements = `http://localhost:8000/api/agreements/${id}`;

  const getItems = async () => {
    const data = await axios
      .get(generateAgreements)
      .then((res) => {
        items.push(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  function onClick(slug, parameters = {}) {
    push(convertSlugUrl(slug, parameters));
  }

  const classes = useStyles();
  return (
    <Column>
      <Row
        horizontal="end"
        className={classes.buttonRow}
      >
        <CreateButton
          label="Create Agreement"
          onClick={() => onClick(SLUGS.selectTemplate + `${id}`)}
        />
      </Row>
      <Row
        horizontal="space-between"
        className={classes.lastRow}
      >
        <TasksComponent
          containerStyles={classes.tasks}
          items={items}
        />
      </Row>
    </Column>
  );
}

export default DashboardComponent;
