import React, {useState, useEffect} from 'react';
import { useForm } from "react-hook-form";
import {
    Input,
    FormGroup,
    Label,
    Button
} from "reactstrap";

import { Table1 } from "./Table";

function ShareAgreement() {

    const { register, handleSubmit, setValue } = useForm();
    const [shareInfo, setShareInfo] = useState([]); 
    const onSubmit = data => alert(JSON.stringify(data, null, 2))

    // useEffect(() => {
    //     register({ name: "shareinfo" });
    // }, [register]);
    
    function addShareRow()
    {
        let l = shareInfo.length;
        const d = [
            ...shareInfo,
            {
                refNum: "#" + l,
                shareName: "",
                shareType: <option>Common</option>,
                shareTotal: 0
            }
        ];
        setValue("shareinfo", d);
        setShareInfo(d); 
    };
    
    function removeShareRow()
    {
        let l = [...shareInfo];
        l.splice(-1, 1);
        setValue("shareinfo", l);
        setShareInfo(l); 
    }

    const shareColumns = [
      {
        Header: "Reference Number",
        accessor: "refNum",
        Cell: ({ row: { index } }) => (
          <Input
            readOnly
            type="text"
            name={`shareinfo[${index}][refNum]`}
            placeholder={accessor}
            {...register(`shareinfo[${index}][refNum]`)}
          />
        ),
      },

      {
        Header: "Share Name",
        accessor: "shareName",
        Cell: ({ row: { index } }) => (
          <Input
            type="text"
            name={`shareinfo[${index}][shareName]`}
            {...register(`shareinfo[${index}][shareName]`)}
          />
        ),
      },

      {
        Header: "Share Type",
        accessor: "shareType",
        Cell: ({ row: { index } }) => (
          <Input
            type="select"
            name={`shareinfo[${index}][shareType]`}
            {...register(`shareinfo[${index}][shareType]`)}
          >
            <option>Common</option>
            <option>Preferred</option>
            <option>Class A</option>
            <option>Class B</option>
            <option>None</option>
          </Input>
        ),
      },

      {
        Header: "Total Shares",
        accessor: "shareTotal",
        Cell: ({ row: { index } }) => (
          <Input
            type="number"
            name={`shareinfo[${index}][shareTotal]`}
            {...register(`shareinfo[${index}][shareTotal]`)}
          />
        ),
      },
    ];
    return (
      <form onSubmit={handleSubmit(onSubmit)}>
        <FormGroup>
          <label>What is your company’s Name?</label>
          <Input type="text" name="companyName" {...register("companyName")} />
        </FormGroup>
        <FormGroup>
          <label>When was your company incorporated?</label>
            <Input
              type="date"
              name="companyDate"
              {...register("companyDate")}
            />
        </FormGroup>
        <FormGroup>
          <Label for="table1">Share Table</Label>
          <Table1 columns={shareColumns} data={shareInfo} />
          <Button onClick={addShareRow}>Add Row</Button>
        </FormGroup>
        <input type="submit" />
      </form>
    );
}

export default ShareAgreement;
