import { useState, useEffect } from "react";
import axios from "axios";

export const useSignup = (validate) =>
{
    const [values, setValues] = useState({
    name: "",
    email: "",
    password: "",
    password2: "",
  });
    
  const [response, setResponse] = useState({
    error: false,
    message: "",
  });

   const [errors, setErrors] = useState({});
    
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [success, setSuccess] = useState({
    state: false,
    message: "",
  });

  const handleRegisterChange = (e) => {
    const { name, value } = e.target;
    setValues({
        ...values,
      [name]:value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const response = {
      error: false,
      message: errors,
    };
    setResponse(response);
    setErrors(validate(values));

    setIsSubmitting(true);
  };

  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmitting) {
      const data = values;
      axios
        .post("http://127.0.0.1:8000/api/register", data)
        .then((res) => {
            const success = {
              state: true,
              message: "Email Verification email sent",
            };
            setSuccess(success);
        })
        .catch((err) => {
            const error = err.response.data;
            const s = Object.values(error.errors);
            s.join(' '); 
            const response = {
                error: true,
                message: s

          };
            setResponse(response);
            setIsSubmitting(false);
        });
    }
  }, [errors]);

  return {
    handleRegisterChange,
    values,
    handleSubmit,
    errors,
    response,
    success
  };
};

export const useLogin = (validate) =>
{
  const [fields, setValues] = useState({
    email: "",
    password: "",
  });

  const [response, setResponse] = useState({
    error: false,
    message: "",
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const history = useHistory();

  const handleLoginChange = (e) => {
    const { name, value } = e.target;
    setValues({
      ...fields,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const response = {
      error: false,
      message: errors,
    };
    setResponse(response);
    setErrors(validate(fields));

    setIsSubmitting(true);
  };

  useEffect(() => {
    if (Object.keys(errors).length === 0 && isSubmitting) {
      const data = fields;
      axios
        .post("http://127.0.0.1:8000/api/login", data)
        .then((res) => {
          localStorage.setItem("token", res.data.token);
          localStorage.setItem("user", res.data.token);
          history.push("/");
        })
        .catch((err) => {
          let errors = "";
          if (err.response.data.errors.email) {
            errors = err.response.data.errors.email;
          } else {
            errors = err.response.data.errors;
          }
          const response = {
            error: true,
            message: errors,
          };
          setResponse(response);
          setIsSubmitting(false);
        });
    }
  }, [errors]);

  return { handleLoginChange, fields, handleSubmit, errors, response };
};
