import React, { useState } from "react";
import { useForm, FormProvider, Controller } from "react-hook-form";
import { Input, FormGroup, Label, Button } from "reactstrap";
import styled from "styled-components";
import { SharesTable, FoundersTable} from "./Table";
import "./test.css";

const Styles = styled.div`
  padding: 1rem;

  table {
    border-spacing: 0;
    border: 1px solid black;

    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }

    th,
    td {
      color: palevioletred
      margin: 0;
      padding: 0.5rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;

      :last-child {
        border-right: 0;
      }

      Input {
        font-size: 1rem;
        padding: 0;
        margin: 0;
        border: 0;
      }
    }
  }

  .pagination {
    padding: 0.5rem;
  }
`;
export function ShareAgreement({methods}) {
  const [shareInfo, setShareInfo] = useState([
    {
      refNum: 1,
      shareName: "",
      shareClass: "",
      shareType: "",
      shareTotal: 0,
    },
  ]);
  
  const [founderInfo, setFounderInfo] = useState([
    {
      refNum: 0,
      fname: "",
      role: "",
      refId: "",
      numHeld: 0,
      addressInfo: "",
      phone: "",
      email: "",
    },
  ]);

  const createAgreement = "http://localhost:8000/api/agreements";
 
  const onSubmit = (data) => {
    console.log(JSON.stringify(data, null, 2));
    console.log(window.location.pathname.split('/')[4])

    const newData = JSON.parse(localStorage.getItem("data"));
    console.log(newData.agreeName)
    // const d = {
    //   name: newDa
    // };
  };

  let i = 0;
  const addShareRow = function () {
    let l = shareInfo.length + 1;
    const d = [...shareInfo];

    const item = {
      refNum: l,
      shareName: "",
      shareClass: "",
      shareType: "",
      shareTotal: 0,
    };
    d.push(item);
    setShareInfo(d);
  };

  const removeShareRow = function () {
    let l = [...shareInfo];
    l.pop();
    setShareInfo(l);
  };

  const shareColumns = [
    {
      Header: "Reference Info",
      columns: [
        {
          Header: "Share ID",
          accessor: "refNum",
        },
      ],
    },
    {
      Header: "Share Info",
      columns: [
        {
          Header: "Share Name",
          accessor: "shareName",
        },
        {
          Header: "Class",
          accessor: "shareClass",
        },
        {
          Header: "Type",
          accessor: "shareType",
        },
        {
          Header: "Total Shares",
          accessor: "shareTotal",
        },
      ],
    },
  ];

  const idata = `shareinfo[${0}]`;
  const [inputList, setInputList] = useState([[{ name: idata, index: 0 }]]);
  
  const addFounderRow = function () {
    let l = founderInfo.length + 1;
    const d = [...founderInfo];
    const item = {
      refNum: l,
      fname: "",
      role: "",
      refId: "",
      numHeld: 0,
      addressInfo: "",
      phone: "",
      email: "",
    };
    d.push(item);
    addInputField();
    setFounderInfo(d);
  };

  const removeFounderRow = function () {
    let l = [...founderInfo];
    i -= 1;
    l.pop();
    removeInputField(); 
    setFounderInfo(l);
  };

  const addInputField = function () {
    const l = inputList.length - 1;
    
    const val = [{ name: idata, index: 0 }]
    inputList.push(val)
    setInputList(inputList); 
    
  }

  const removeInputField = function () {
    inputList.pop()
    setInputList(inputList)
  }

  const founderColumns = [
    {
      Header: "Reference Info",
      columns: [
        {
          Header: "Person ID",
          accessor: "refNum",
        },
      ],
    },
    {
      Header: "Shares Held",
      columns: [
        {
          Header: "Reference #",
          accessor: "refID",
        },

        {
          Header: "# Held",
          accessor: "numHeld",
        },
      ],
    },
    {
      Header: "Personal Info",
      columns: [
        {
          Header: "Full Name",
          accessor: "fname",
        },
        {
          Header: "Role",
          accessor: "role",
        },
        {
          Header: "Address",
          accessor: "addressInfo",
        },

        {
          Header: "Phone Number",
          accessor: "phone",
        },

        {
          Header: "Email(Optional)",
          accessor: "email",
        },
      ],
    },
    {
      Header: "Corporation Info",
      columns: [
        {
          Header: "On the Board of Directors",
          accessor: "isBoard",
        },
        {
          Header: "Corporation Name (If Applicable)",
          accessor: "corpName",
        },
      ],
    },
  ];

  return (
    <FormProvider {...methods}>
      <form onSubmit={methods.handleSubmit(onSubmit)}>
        <FormGroup>
          <label>What is your company’s Name?</label>
          <Input type="text" name="companyName" innerRef={methods.register} />
        </FormGroup>
        <FormGroup>
          <label color="black">What is your company's place of incorporation?</label>
          <Input type="select" name="companyPlace" innerRef={methods.register}>
            <option>Canada</option>
            <option>Ontario</option>
          </Input>
        </FormGroup>
        <FormGroup>
          <label>When was your company incorporated?</label>
          <Input
            type="date"
            name="companyDate"
            innerRef={methods.register}
          />
        </FormGroup>

        <FormGroup>
          <Label for="sTable">What Does Your Company Have?</Label>
          <div className="Table">
            <Controller
              name="sTable"
              control={methods.control}
              defaultValue="name"
              as={
                <Styles>
                  <SharesTable
                    columns={shareColumns}
                    ref={methods.register}
                    data={shareInfo}
                    methods={methods}
                  />
                </Styles>
              }
            />
          </div>
          <Button onClick={addShareRow}>Add Row </Button>{" "}
          <Button onClick={removeShareRow}>Remove Row</Button>
        </FormGroup>
        <FormGroup>
          <Label>Who are the Stakeholders?</Label>
          <div className="Table">
            <Styles>
              <FoundersTable
                columns={founderColumns}
                data={founderInfo}
                ref={methods.register}
                methods={methods}
                inputList={inputList}
                setInputList={setInputList}
              />
            </Styles>
          </div>
          <Button onClick={addFounderRow}>Add Row </Button>{" "}
          <Button onClick={removeFounderRow}>Remove Row</Button>
        </FormGroup>
        <input type="submit" />
      </form>
    </FormProvider>
  );
}

export default ShareAgreement;
