    const [founderInfo, setFounderInfo] = useState([]);
    const [corpInfo, setCorpInfo] = useState([]);
    const [values, setValues] = useState();
    const methods = useForm();
    const onSubmit = (data) => {
        console.log(JSON.stringify(data, null, 2));
    }
    
    React.useEffect(() => {
        methods.register({ name: "shareInfo" })
    }, [methods])
    

    const addShareRow = function () {
        let l = shareInfo.length + 1;
        const d = [...shareInfo];


        const item = {
            refNum: l,
            shareName: "",
            shareClass: "",
            shareType: "",
            shareTotal: 0,
        };
        list.push(item.refNum);
        d.push(item);
        setShareInfo(d);
        console.log(shareInfo)
        
    }

    const removeShareRow = function(){
        let l = [...shareInfo];
        //l.splice(-1, 1);
        l.pop();
        
        if (list[0] != null) {
            list.pop();
        }
        //reset(`shareInfo[${-1}][shareName]`);
        //unregister(`shareinfo[${-1}][shareName]`);
        setShareInfo(l);
            
    };
    const overrideValue = (index, override) => {
        let name = [...shareInfo]
        name[index].shareName = override;
        setShareInfo({ shareInfo }); 
    }
    
    const shareColumns = [
      {
        Header: "Reference Number",
        accessor: "refNum",
      },

      {
        Header: "Share Name",
        accessor: "shareName",
        Cell: (props) => (
          <Input
            type="text"
            name={`shareInfo[${props.index}][shareName]`}
            onChange={e => overrideValue(props.index, e.target.value)}
            innerRef={methods.register({required: true})}
          />
        ),
      },

      {
        Header: "Share Type",
        columns: [
          {
            Header: "Class",
            accessor: "shareClass",
            Cell: ({ row: { index } }) => (
              <Input
                type="select"
                name={`shareInfo[${index}][shareClass]`}
                innerRef={methods.register}
              >
                <option>Class A</option>
                <option>Class B</option>
                <option>None</option>
              </Input>
            ),
          },
          {
            Header: "Type",
            accessor: "shareType",
            Cell: ({ row: { index } }) => (
              <Input
                type="select"
                name={`shareinfo[${index}][shareType]`}
                // innerRef={register}
              >
                <option>Common</option>
                <option>Preferred</option>
              </Input>
            ),
          },
        ],
      },

    //   {
    //     Header: "Total Shares",
    //     accessor: "shareTotal",
    //     Cell: ({ row: { index } }) => (
    //       <Input
    //         type="number"
    //         name={`shareinfo[${index}][shareTotal]`}
    //         // innerRef={register}
    //       />
    //     ),
    //   },
    ];

    const addFounderRow = function () {
        let l = founderInfo.length + 1;
        const d = [...founderInfo];

        const item = {
        fname: "",
        refId: "",
        numHeld: 0,
        streetName: "",
        cityName: "",
        provinceName: "",
        postalCode: "",
        phone: "",
        email: "",
        };
        d.push(item); 
        setFounderInfo(d);
    };

    // const founderColumns = [
    //   {
    //     Header: "Full Name",
    //     accessor: "fname",
    //     Cell: ({ row: { index } }) => (
    //       <NiceInput
    //         type="text"
    //         control={control}
