    //           />
    //         ),
    //       },
    //     ],
    //   },

    //   {
    //     Header: "Address",
    //     columns: [
    //       {
    //         Header: "Street Name",
    //         accessor: "streetName",
    //         Cell: ({ row: { index } }) => (
    //           <Input
    //             type="text"
    //             name={`founderinfo[${index}][streetName]`}
    //             innerRef={register}
    //           />
    //         ),
    //       },
    //       {
