import React, {ComponentProps} from "react";
import { Input } from "reactstrap";
import { InputType } from "reactstrap";
import { useController, useForm } from "react-hook-form";

export default function NiceInput({ control, name}) {
  const {
    field: { ref, ...inputProps },
    fieldState: { invalid, isTouched, isDirty },
    formState: { touchedFields, dirtyFields },
  } = useController({
    name,
    control,
    rules: { required: true },
    defaultValue: "",
  });

  return <Input {...inputProps} />;
}

export const NiceInput1 = React.forwardRef(({ control, name}, ref) =>
{
    const {
      field: { refs, ...inputProps },
      
    } = useController({
      name,
      control,
      rules: { required: true },
      defaultValue: "",
    });

    return (
      <Input
        {...inputProps}
        inputref={refs}
      />
    );

});

export const NiceSelectInput = React.forwardRef(({ control, name, options }, ref) => {
  const {
    field: { refs, ...inputProps },
  } = useController({
    name,
    control,
    rules: { required: true },
    defaultValue: "",
  });

    return (
        <select type="select" {...inputProps} inputref={refs}>
            {options.map(value => (
        <option key={value} value={value}>
          {value}
                </option>
    
            ))}
        </select>
        );

});
