import React, { useEffect, useState } from "react";
import { Column, Row } from "simple-flexbox";
import CreateButton from "../../../components/CreateButton";
import { createUseStyles } from "react-jss";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { Paper } from "@material-ui/core";
import { useParams } from "react-router";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import SLUGS from "../../../resources/slugs";
import {
  Beginning,
  Recitals,
  Articles,
  Article2,
  Article4,
  Article6,
  Article7,
  Article8,
  Article10,
  Article10b,
  Article9,
} from "./SharePages";
import {
  PDFDownloadLink,
  Page,
  Text,
  View,
  Document,
  StyleSheet,
} from "@react-pdf/renderer";
import { Html } from "react-pdf-html";

const useStyles = createUseStyles({
  lastRow: {
    marginTop: 10,
  },

  paperStyle: {
    background: "white",
    paddingTop: 60,
    paddingLeft: 30,
    paddingRight: 30,
    paddingBottom: 60,
    marginLeft: "min(60, 5%)",
    // marginRight: 60,
    marginBottom: 50,
  },
  container: {
    width: "100%",
  },

  buttonRow: {
    marginTop: 10,
  },

  title: {
    marginTop: 10,
  },
  paragraph: {
    whiteSpace: "pre-line",
  },

  rowC: {
    display: "flex",
    flexDirection: "row"
}, 

  a: {
    position: "relative",
    display: "inline-block",
    fontSize: 16,
    lineHeight: 24,
    height: 24,
    color: "rgb(0, 0, 191)",
    textDecoration: "none",
    "&::after": {
      content: "",
      position: "absolute",
      display: "block",
      bottom: 0,
      left: "50%",
      width: 0,
      height: 2,
      borderBottom: "2px solid rgb(255, 0, 0)",
    },
    "&, &::after": {
      transition: "all 0.6s linear",
    },
    "&:hover": {
      color: "rgb(255, 0, 0)",
    },
    "&:hover::after": {
      left: 0,
      width: "100%",
    },
  },
});

const styles = StyleSheet.create({
  body: {
    paddingTop: 35,
    paddingBottom: 65,
    paddingHorizontal: 35,
  },
  title: {
    fontSize: 24,
    textAlign: "center",
    fontFamily: "Oswald",
  },
  author: {
    fontSize: 12,
    textAlign: "center",
    marginBottom: 40,
  },
  subtitle: {
    fontSize: 18,
    margin: 12,
    fontFamily: "Oswald",
  },
  text: {
    margin: 12,
    fontSize: 14,
    textAlign: "justify",
    fontFamily: "Times-Roman",
  },
  image: {
    marginVertical: 15,
    marginHorizontal: 100,
  },
  header: {
    fontSize: 12,
    marginBottom: 20,
    textAlign: "center",
    color: "grey",
  },
  pageNumber: {
    position: "absolute",
    fontSize: 12,
    bottom: 30,
    left: 0,
    right: 0,
    textAlign: "center",
    color: "grey",
  },
});
export function CreateShareAgreement() {
  const { id } = useParams();
  const { push } = useHistory();

  const classes = useStyles();
  const token = sessionStorage.getItem("xrsf");
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };
  const api = `http://localhost:8000/api/agreements/${id}`;

  const [formData, setFormData] = useState([]);
  const [isLoading, setIsLoading] = useState({
    loading: false,
  });

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    setIsLoading(true);
    const d = await axios
      .get(api, config)
      .then((res) => {
        setFormData(res.data);
        console.log(res);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        push(SLUGS.agreements);
      });
  };

  const name = "Sign & Download";
  return !isLoading ? (
    <div>
      <Row>
        <Column
          className={[classes.container, classes.title].join(" ")}
          horizontal="start"
        >
          {formData && formData[0] && <h1>{formData[0].name}</h1>}
        </Column>
        <Column
          className={classes.container}
          vertical="center"
          horizontal="end"
        >
          <div className={classes.rowC}>
            <IconButton aria-label="back">
              <ArrowBackIcon />
            </IconButton>
            <IconButton aria-label="edit">
              <EditIcon />
            </IconButton>
            <IconButton aria-label="delete">
              <DeleteIcon />
            </IconButton>
            <IconButton aria-label="back">
              <MoreVertIcon />
            </IconButton>
            <CreateButton label={name} />
          </div>

          {/* <Show data={formData}/> */}
        </Column>
      </Row>
      <Row>
        <Row className={classes.lastRow}>
          <DisplayData data={formData} />
        </Row>
      </Row>
    </div>
  ) : (
    <div>Loading...</div>
  );
}

function DisplayData({ data }) {
