import React, { useState, useEffect, useCallback } from "react";
import { useForm, FormProvider, Controller } from "react-hook-form";
import { Input, FormGroup, Label, Button } from "reactstrap";
import styled from "styled-components";
import Select from "react-select";
import countryList from "react-select-country-list";
import { SharesTable, FoundersTable, CorpsTable } from "./Table";
import "./test.css";
import checkInput from "./Table/FoundersTable"; 

const Styles = styled.div`
  padding: 1rem;

  table {
    border-spacing: 0;
    border: 1px solid black;

    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }

    th,
    td {
      margin: 0;
      padding: 0.5rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;

      :last-child {
        border-right: 0;
      }

      Input {
        font-size: 1rem;
        padding: 0;
        margin: 0;
        border: 0;
      }
    }
  }

  .pagination {
    padding: 0.5rem;
  }
`;


export let rowAdded = false; 
function ShareAgreement() {
  const [shareInfo, setShareInfo] = useState([
    {
      refNum: 1,
      shareName: "",
      shareClass: "",
      shareType: "",
      shareTotal: 0,
    },
  ]);
  
  const [founderInfo, setFounderInfo] = useState([
    {
      refNum: 0,
      fname: "",
      role: "",
      refId: "",
      numHeld: 0,
      addressInfo: "",
      phone: "",
      email: "",
    },
  ]);
 
  const options = countryList().getData()
  const methods = useForm();
  const onSubmit = (data) => {
    console.log(JSON.stringify(data, null, 2));
  };

  let i = 0; 
  const addShareRow = function () {
    let l = shareInfo.length + 1;
    const d = [...shareInfo];
    
    const item = {
      refNum: l,
      shareName: "",
      shareClass: "",
      shareType: "",
      shareTotal: 0,
    };
    d.push(item);
    setShareInfo(d);
    checkInput(l)
  };

  const removeShareRow = function () {
    let l = [...shareInfo];
    l.pop();
    setShareInfo(l);
  };

  const shareColumns = [
    {
      Header: "Reference Info",
      columns: [
        {
          Header: "Share ID",
          accessor: "refNum",
        },
      ]
    },
    {
      Header: "Share Info",
      columns: [
        {
          Header: "Share Name",
          accessor: "shareName",
        },
        {
          Header: "Class",
          accessor: "shareClass",
        },
        {
          Header: "Type",
          accessor: "shareType",
        },
        {
          Header: "Total Shares",
          accessor: "shareTotal",
        },
      ],
    },
  ];

  const addFounderRow = function () {
    let l = founderInfo.length + 1;
    const d = [...founderInfo];
    rowAdded = true; 
    const item = {
      refNum: l,
      fname: "",
      // role: "",
      // refId: "",
      // numHeld: 0,
      // addressInfo: "",
      // phone: "",
      // email: "", 
    };
    d.push(item);
    setFounderInfo(d);
  };

  const removeFounderRow = function () {
    let l = [...founderInfo];
    i -= 1; 
    l.pop();
    setFounderInfo(l);
  };

  const founderColumns = [
    {
      Header: "Reference Info",
      columns: [
        {
          Header: "Person ID",
          accessor: "refNum",
        },
      ],
    },
    {
      Header: "Shares Held",
      columns: [
        {
          Header: "Reference #",
          accessor: "refID",
        },

        {
          Header: "# Held",
          accessor: "numHeld",
        },
      ],
    },
    {
      Header: "Personal Info",
      columns: [
        {
          Header: "Full Name",
          accessor: "fname",
        },
        {
          Header: "Role",
          accessor: "role",
        },
        {
          Header: "Address",
          accessor: "addressInfo",
        },

        {
          Header: "Phone Number",
          accessor: "phone",
        },

        {
          Header: "Email(Optional)",
          accessor: "email",
        },
      ],
    },
    {
      Header: "Corporation Info",
      columns: [
        {
          Header: "On the Board of Directors",
          accessor: "isBoard",
        },
        {
          Header: "Corporation Name (If Applicable)",
          accessor: "corpName",
        }
      ],
    },
  ];

  return (
    <FormProvider {...methods}>
      <form onSubmit={methods.handleSubmit(onSubmit)}>
        <FormGroup>
          <label>What is your companyâ€™s Name?</label>
          <Input type="text" name="companyName" innerRef={methods.register} />
        </FormGroup>
        <FormGroup>
          <label>What is your company's place of incorporation?</label>
          <Select
            name="companyCountry"
            options={options}
            inputRef={methods.register}
            //method={methods.register}
          />
        </FormGroup>
        <FormGroup>
          <label>When was your company incorporated?</label>
          <Input
            type="date"
            name="companyDate"
            innerRef={methods.register}
            //method={methods.register}
          />
        </FormGroup>

        <FormGroup>
          <Label for="sTable">What Does Your Company Have?</Label>
          <div className="Table">
            <Controller
              name="sTable"
              control={methods.control}
              defaultValue="name"
              as={
                <Styles>
                  <SharesTable
                    name="sTable"
                    columns={shareColumns}
                    ref={methods.register}
                    data={shareInfo}
                    methods={methods}
                  />
                </Styles>
              }
            />
          </div>
          <Button onClick={addShareRow}>Add Row </Button>{" "}
          <Button onClick={removeShareRow}>Remove Row</Button>
        </FormGroup>
        <FormGroup>
          <Label>Who are the Stakeholders?</Label>
          <div className="Table">
            <Styles>
              <FoundersTable
                columns={founderColumns}
                data={founderInfo}
                ref={methods.register}
                methods={methods}
              />
            </Styles>
          </div>
          <Button onClick={addFounderRow}>Add Row </Button>{" "}
          <Button onClick={removeFounderRow}>Remove Row</Button>
        </FormGroup>
        <input type="submit" />
      </form>
    </FormProvider>
  );
}

export default ShareAgreement;
