import React, {useContext, push} from 'react';
import { string } from 'prop-types';
import { Row } from 'simple-flexbox';
import { createUseStyles, useTheme } from 'react-jss';
import { SidebarContext } from '../Sidebar/useSidebar';
import DropdownComponent from '../Dropdown/DropdownComponent';
import SLUGS from '../../resources/slugs';
import standIn from '../../assets/hourglass-icon.jpg';
import { convertSlugUrl } from '../../resources/utils';

const useStyles = createUseStyles((theme) => ({
  avatar: {
    height: 35,
    width: 35,
    minWidth: 35,
    borderRadius: 50,
    marginRight: 10,
    border: `1px solid ${theme.color.lightGrayishBlue2}`,
    "@media (max-width: 768px)": {
      marginLeft: 7,
    },
  },
  container: {
    height: 90,
    marginBottom: 30,
    backgroundColor: "#FFFFFF",
    boxShadow: "5px 5px 5px 0 rgba(0,0,0,0.08)",
  },
  name: {
    ...theme.typography.itemTitle,
    textAlign: "right",
    "@media (max-width: 768px)": {
      display: "none",
    },
    marginRight: 35,
  },
  title: {
    ...theme.typography.title,
    "@media (max-width: 1080px)": {
      marginLeft: 50,
    },
    "@media (max-width: 468px)": {
      fontSize: 20,
    },
  },
  iconStyles: {
    cursor: "pointer",
    marginLeft: 25,
    "@media (max-width: 768px)": {
      marginLeft: 12,
    },
  },
}));

function HeaderComponent()
{
  const currentItem = useContext(SidebarContext);
  const theme = useTheme();
  const classes = useStyles({ theme }); 
  let title;
  switch (true) {
    case currentItem === SLUGS.agreements:
      title = "Agreements";
      break;
    case currentItem === SLUGS.templates:
      title = "Templates";
      break;
    case currentItem === SLUGS.lawyers:
      title = "Lawyers";
      break;
    case currentItem === SLUGS.supportTickets:
      title = "Support Tickets";
      break;
    case currentItem === SLUGS.settings:
      title = "Settings";
      break;
    default:
      title = "";
  }
  function onClick(slug, parameters = {}) {
      push(convertSlugUrl(slug, parameters));
  }
  
  function logout() {
    localStorage.clear();
    onClick(SLUGS.login); 
  }
    return (
      <Row
        className={classes.container}
        vertical="center"
        horizontal="space-between"
      >
        <span className={classes.title}>{title}</span>
        <Row vertical="center">
          <DropdownComponent
            label={
              <>
                <img src={standIn} alt="avatar" className={classes.avatar} />
                <span className={classes.name}>Richard Mba</span>
              </>
            }
            options={[
              {
                label: "Edit Profile",
                onClick: () => onClick(SLUGS.settings),
              },

              {
                label: "Logout",
                onClick: () => logout(),
              },
            ]}
            position={{
              top: 52,
              right: 20,
            }}
          />
        </Row>
      </Row>
    );

}

HeaderComponent.propTypes = {
    title: string
};

export default HeaderComponent;
