import React, { useEffect, useState} from "react";
import { Column, Row } from "simple-flexbox";
import CreateButton from "../../../components/CreateButton";
import { createUseStyles } from "react-jss";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { Paper } from "@material-ui/core"; 
import { useParams } from "react-router";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import SLUGS from "../../../resources/slugs";
import { ToWords } from "to-words";
import {
  Recitals,
  Article2,
  Article7,
  Article8,
  Article10,
  Article10b,
  Article9
} from "./SharePages";


const useStyles = createUseStyles({
  lastRow: {
    marginTop: 10,
  },

  paperStyle: {
    background: "white",
    paddingTop: 60,
    paddingLeft: 30,
    paddingRight: 30,
    paddingBottom: 60,
    marginLeft: "min(60, 5%)",
    // marginRight: 60,
    marginBottom: 50,
  },
  container: {
    width: "100%",
  },

  buttonRow: {
    marginTop: 10,
  },

  title: {
    marginTop: 10,
  },
  paragraph: {
    whiteSpace: "pre-line",
  },

  a: {
    position: "relative",
    display: "inline-block",
    fontSize: 16,
    lineHeight: 24,
    height: 24,
    color: "rgb(0, 0, 191)",
    textDecoration: "none",
    "&::after": {
      content: "",
      position: "absolute",
      display: "block",
      bottom: 0,
      left: "50%",
      width: 0,
      height: 2,
      borderBottom: "2px solid rgb(255, 0, 0)",
    },
    "&, &::after": {
      transition: "all 0.6s linear",
    },
    "&:hover": {
      color: "rgb(255, 0, 0)",
    },
    "&:hover::after": {
      left: 0,
      width: "100%",
    },
  },
});

export function CreateShareAgreement() {

  const { id } = useParams();
  const { push } = useHistory(); 

  const classes = useStyles(); 
  const token = sessionStorage.getItem("xrsf");
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };
  const api = `http://localhost:8000/api/agreements/${id}`;

  const [formData, setFormData] = useState([]);
  const [isLoading, setIsLoading] = useState({
    loading: false
  })

  useEffect(() => {
    getData();
  }, [])

  const getData = async () => {
    setIsLoading(true); 
    const d = await axios
      .get(api, config)
      .then((res) => {
        setFormData(res.data);
        console.log(res);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        push(SLUGS.agreements)
      })
  }

  const name = 'Sign & Download'
  return !isLoading ? (
    <div>
      <Row>
        <Column
          className={[classes.container, classes.title].join(" ")}
          horizontal="start"
        >
          {formData && formData[0] && <h1>{formData[0].name}</h1>}
        </Column>
        <Column
          className={classes.container}
          vertical="center"
          horizontal="end"
        >
          <CreateButton label={name} />
        </Column>
      </Row>
      <Row className={classes.buttonRow} horizontal="end">
        <div>
          <IconButton aria-label="back">
            <ArrowBackIcon />
          </IconButton>
          <IconButton aria-label="edit">
            <EditIcon />
          </IconButton>
          <IconButton aria-label="delete">
            <DeleteIcon />
          </IconButton>
          <IconButton aria-label="back">
            <MoreVertIcon />
          </IconButton>
        </div>
      </Row>
      <Row>
        <Row className={classes.lastRow}>
          <DisplayData data={formData} />
        </Row>
      </Row>
    </div>
  ) : (
    <div>Loading...</div>
  );
}

function DisplayData({ data } )
{
  
  const createdDate = data[0].created_at; 
  const classes = useStyles(); 
  const val = JSON.parse(data[0].data);
  
  const corpName = val.companyName;
  const corpPlace = val.companyPlace;
  const corpDate = val.companyDate;
  const corpFax = val.companyFax;
  const corpContact = val.companyContact;
  
  const shareData = [];
  const personData = [];
  const founderData = [];
  const directorData = []; 
  
  
  
  val.shareInfo.map((x, i) => {
    return (shareData[i] = {
      shareID: x.shareID,
      shareName: x.shareName,
      shareClass: x.shareClass,
      shareType: x.shareType,
      shareTotal: x.shareTotal
    });
  });

  val.founderInfo.map((x, i) => {
    return (personData[i] = {
      name: {
        fname: x.fname,
        prefName: x.preferredName
      },
      phone: x.phone,
      email: x.email,
      address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
      IsCompany: [x.role, x.corpName],
      Ownership: x.shareinfo,
      IsBoard: x.onBoard,
      shareinfo: x.shareinfo
    });
  });

  val.founderInfo.map((x, i) => {
    if (x.role === "Founder") {
        founderData.push({
          name: {
            fname: x.fname,
            prefName: x.preferredName
          },
          phone: x.phone,
          email: x.email,
          address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
          IsCompany: [x.role, x.corpName],
          Ownership: x.shareinfo,
          IsBoard: x.onBoard,
          shareinfo: x.shareinfo
        })
    };
  });

  val.directInfo.map((x, i) => {
    return (directorData[i] = {
      name: x.name,
      nominee: x.nominee,
      shareinfo: x.shareinfo
    })
  });
  
  const Beginning = function() {
  
    return (
      <div>
        <h6 style={{}}>SHAREHOLDERS AGREEMENT OF {corpName.toUpperCase()}</h6>
        <p>
          THIS AGREEMENT (the “Agreement”), is made as of {corpDate}, between{" "}
          {corpName}, a corporation incorporated under the laws of {corpPlace}{" "}
          (the “Corporation”);
          {personData.map((x) => {
            return `
              ${x.name.fname},
              an individual residing in the City of
              ${x.address[2]},
              ${
                !x.name.prefName
                  ? `${x.address[1]};`
                  : `${x.address[1]} ("${x.name.prefName}");`
              } `;
          })}
          each investor being listed on Schedule “A” annexed hereto, and any
          person who becomes a party hereto by executing an acknowledgement in
          the form annexed hereto as Schedule “B”.
        </p>
      </div>
    );
  }


  // console.log(beginning())

  const Articles = function () {
    const corpAct = corpPlace === "Canada" ? `CBCA ("Canada Business Corporations Act")` :
      "OBCA (Business Corporations Act of Ontario)";
    const shareTypes = [];
    shareData.map((x , i) => {
      shareTypes[i] = `"${x.shareName}" means the Class ${x.shareClass} ${x.shareType} 
        in the capital of the corporation`
    });

    const founder =
      founderData.length > 0 ?
      founderData.length > 1
        ? "Founders means " +
          `${founderData.map((x, i) => {
            return `${
              i == founderData.length - 1
                ? ` and ${x.name.fname}`
                : `${x.name.fname}`
            }`;
          })}` +
        ' \n and "Founder" shall mean any one of them' :
          `\n Founder means ${founderData[0].name.fname}`
        : " "
    
    const control = `“Control” means: (a) with respect to any corporation, 
    the ownership, beneficially and legally of voting securities in the 
    capital of such corporation, to which are attached more than fifty(50 %) 
    of the votes that may be cast to elect the directors of such corporation 
    and such votes are sufficient(if exercised) to elect a majority of the directors;
    and(b) with respect to a partnership, trust, syndicate, or other entity,
      actual power or authority to manage and direct the affairs of,
        or ownership of more than fifty(50 %) of the beneficial interest of such entity.`;
    
    return (
      <div>
        <Header
          title="ARTICLE 1: DEFINITION"
          classes={classes}
        />
        <p>
          Whenever and wherever used in this Agreement, the following words and
          terms shall have the following meanings as set out below:
        </p>
        <ol>
          <li>
            “Act” means the {corpAct} as the same may be amended from time to
            time;
          </li>
          <li>“Affiliate” of a Person means another Person where:</li>
          <ol>
            <li>One of them is a Subsidiary of the other, or</li>
            <li>Each of them is controlled by the same Person.</li>
          </ol>
          <li>
            “Agreement” means this Shareholder’s Agreement and all attached
            schedules and all instruments supplemental to or in amendment or
            confirmation of this Agreement;
          </li>
          <li>
            “arm’s length” has the meaning that is has for the purposes of the
            Income Tax Act (Canada);{" "}
          </li>
          <li>
            “Articles” means the articles of incorporation of the Corporation,
            as amended from time to time;
          </li>
          <li>
            “Board” means the board of directors of the Corporation, as
            constituted from time to time in accordance with this Agreement;
          </li>
          <li>
            “Business Day” means any day of the week except Saturday, Sunday, or
            any statutory holiday in the Province of Ontario;
          </li>
          {shareTypes.map((x, i) => {
            return <li key={i}>{x}</li>;
          })}
          <li>
            “Competitor” means any business which is, at the relevant time,
            engaged in the creation, design, development, marketing, sale,
            licensing, or other business activity of any products or services
            that are in form and functionality competitive with any of the
            products or services that are marketed and sold or licensed by the
            Corporation or any product or service that is known by a Shareholder
            to be under development by the Corporation;
          </li>
          <li>{control}</li>
          <li>
            “Family Members” means any parent, spouse, child, spouse of a child,
            grandchild, or sibling of an individual;
          </li>
          <li>{founder}</li>
          <li>
            “Founder Shares” means the Common Shares held by the Founders;
          </li>
          <li>
            “Initial Public Offering” or “IPO” shall mean the Corporation’s
            first underwritten public offering of its Common Shares on any stock
            exchange or market approved in writing by a Preferred Majority;
          </li>
          <li>
            “Investor” means each Person listed in Schedule A of this Agreement
            under the heading “Investors”, so long as such Person holds Class A
            Preferred Shares, together with any other Person that becomes a
            Party to this Agreement as a holder of Class A Preferred Shares
            (including Permitted Transferees of Class A Preferred Shares); and
            “Investor” means all of such Persons collectively;
          </li>
        </ol>
        <h6>1.2 Additional Definitions</h6>
        <p>
          Unless there is an inconsistency in the subject matter or context, or
          unless otherwise provided for in this Agreement, all other words and
          terms used herein that are defined in the Act have the meanings set
          out in that Act.{" "}
        </p>
        <h6>1.1 Rules of Interpretation</h6>
        <p>
          As used in this Agreement, the following rules shall apply to the use
          of the following terms:
        </p>
        <ol>
          <li>
            Consent. Where a provision of this Agreement requires an approval or
            consent and such approval or consent is not delivered within the
            applicable time limit, then unless otherwise specified, the Party
            whose consent or approval is required is conclusively deemed to have
            withheld its approval or consent.
          </li>
          <li>
            Including. Where the word “including” or the word “includes” is used
            in this Agreement, it means “including (or includes) without
            limitation”
          </li>
          <li>
            Number and Gender. Unless the context otherwise necessitates, words
            using the singular include the plural and vice versa, and words
            importing gender shall include all genders.
          </li>
          <li>
            Severability. In the event that one or several of the provisions of
            this Agreement are found to be invalid, illegal, or unenforceable in
            any respect in accordance with any laws or regulations which have
            jurisdiction over said Agreement, the validity, legality, and or
            enforceability of the remaining provisions of this Agreement shall
            not be affected or compromised in any respect. The Parties shall
            strive in good faith to replace such invalid, illegal, or
            unenforceable provisions with effective provisions that accomplish
            to the greatest extent permitted by law and the intentions of the
            Parties, and the economic effect of such effective provisions shall
            be as close as possible to the economic effect of those invalid,
            illegal, or unenforceable provisions.
          </li>
          <li>
            Time Periods. Unless otherwise specified, time periods within or
            following which any payment is to be made or act is to be done will
            be calculated by excluding the day on which the period commences and
            including the day on which the period ends and by extending the
            period to the following Business Day if the last day of the period
            is not a Business Day.
          </li>
          <li>
            Currency. Unless otherwise indicated, all dollar amounts in this
            Agreement shall refer to lawful money of Canada.
          </li>
        </ol>
      </div>
    ); 
  }

  
  
  function getClass(shareID, numHeld) {
    
    let c = ""; 
    for (let i = 0; i < shareData.length; i++)
    {
      if (shareID === shareData[i].shareID && numHeld > 0) {
        c = shareData[i].shareClass;
        return c; 
      }
    }
     return c;
  }

  function strigifyNums(num) {
    const toWords = new ToWords();
    let words = toWords.convert(num).toLowerCase();
    return words; 
  }
  
  const Article4 = function () {
    const prefShares = [];

    const toWords = new ToWords()
    let numDirectors = toWords.convert(directorData.length).toLowerCase()
    const directWords = directorData.length > 1 ? "directors" : "director"
    const directors = {
      founder: {
        number: 0,
        names: "",
        nominee: "Founder "
      },
      preferred: {
        number: 0,
        name: "",
        nominee: "Investor ", 
        classA: {
          number: 0,
          names: "", 
          nominee: "Class A Investor "
        },

        classB: {
          number: 0,
          names: "",
          nominee: "Class B Investor "
        }
      },
      common: {
        number: 0, 
        names: "",
        nominee: "Common "
      }
    }
    
    shareData.map((x) => {
      if (x.shareType === "Preferred")
      {
        prefShares.push({
          id: x.shareID,
          name: x.shareName,
          class: x.shareClass, 
        })
        }
    });

    directorData.map((x, i) => {
      
      if (x.nominee === "Founder") {

        directors.founder.number += 1
        directors.founder.names += " " + `${x.name},`;
      }

      if (x.nominee === "Preferred") {
        directors.preferred.number += 1
        directors.preferred.name = x.name

        directorData[i].shareinfo.map((n) => {
          const c = getClass(n.shareID, n.numHeld)
          if (c === "Class A") {
            directors.preferred.classA.number += 1
            directors.preferred.classA.names += " " + `${x.name},`;
          }
          if (c === "Class B") {
            directors.preferred.classB.number += 1;
            directors.preferred.classB.names += " " + `${x.name},`;
          }
        })
      }

      if (x.nominee === "Common") {
        directors.common.number += 1
        directors.common.names += " " + `${x.name},`;
      }

    });
    console.log(directorData[0])
    const paragraphPref =
      `Any amendment to the Articles or by-laws of the Corporation that would adversely affect the rights, 
      preferences, privileges, or powers of` +
      `
      ${prefShares.length > 1 ?
        `${prefShares.map((x, i) => {
          return `${i == prefShares.length - 1 ? ` and ${x.name}` : `${x.name}`}`;
        })}`
        :
        `${prefShares[0].name}`
      }`
    return (
      <div>
        <h6>ARTICLE 4: STRUCTURE OF THE CORPORATION</h6>
        <h6>4.1 Board of Directors</h6>

        <p>
          The Corporation shall have a Board of Directors consisting of up to{" "}
          {numDirectors} {directWords}, of which:
        </p>
        <ol type="a">
          {directors.founder.number > 0 && (
            <li>
              {strigifyNums(directors.founder.number)} director(s) shall be nominated by the
              Founders (the “{directors.founder.nominee}
              Nominee”) who shall initially be {directors.founder.names}
            </li>
          )}
          {directors.preferred.number === 1 && (
            <li>
              {strigifyNums(directors.preferred.number)} director(s) shall be
              nominated by the Founders (the “{directors.preferred.nominee}
              Nominee”) who shall initially be {directors.preferred.name}
            </li>
          )}
          {directors.preferred.number > 1 &&
            directors.preferred.classA.number > 0 && (
              <li>
                {strigifyNums(directors.preferred.classA.number)} director(s)
                shall be nominated by the Founders (the “
                {directors.preferred.classA.nominee}
                Nominee”) who shall initially be{" "}
                {directors.preferred.classA.names}
              </li>
            )}
          {directors.preferred.number > 1 &&
            directors.preferred.classB.number > 0 && (
              <li>
                {strigifyNums(directors.preferred.classB.number)} director(s)
                shall be nominated by the Founders (the “
                {directors.preferred.classB.nominee}
                Nominee”) who shall initially be{" "}
                {directors.preferred.classB.names}
              </li>
            )}
          {directors.common.number > 0 && (
            <li>
              {strigifyNums(directors.common.number)} director(s) shall be nominated by the
              Founders (the “{directors.common.nominee}
              Nominee”) who shall initially be {directors.common.names}
            </li>
          )}
        </ol>
        <p>
          Each Shareholder shall vote its Shares at any meeting where directors
          are being elected, or execute any written resolutions of the
          shareholders at the request of the Corporation, to elect the directors
          nominated in accordance with this Agreement.
        </p>
        <h6>4.2 Removal and Replacement of Nominees</h6>
        <p>
          Any Shareholder (or group of Shareholders) entitled nominate and elect
          a director may remove any such director by notice to such director,
          the other Shareholders and the Corporation. Any vacancy occurring on
          the Board by reason of the death, disqualification, inability to act,
          resignation or removal of any director may be filled only by a further
          nominee of the Shareholder or Shareholders whose nominee was so
          affected so as to maintain a Board consisting of the nominees
          specified in Section 4.1.{" "}
        </p>
        <h6>4.3 Matters Requiring Prior Approval</h6>
        <p>
          In addition to any approval, authorization, or ratification required
          by the Act, none of the following shall be carried out and executed by
          the Corporation without the prior approval of the Board and prior
          written approval of the Preferred Majority.{" "}
        </p>
        <ol type="a">
          <li>{paragraphPref}</li>
          <li>Any changes to the number of directors of the Corporation;</li>
          <li>
            Any corporate restructuring, amalgamation or merger of the
            Corporation with any other corporation; or
          </li>
          <li>
            The disposition of all or substantially all of the assets of any
            wholly owned subsidiary of the Corporation or the disposition of any
            shares of any wholly owned subsidiary of the Corporation.
          </li>
        </ol>
        <p>4.4 Meetings</p>
        <ol type="A">
          <li>
            Generally. The Board shall meet at such time and at such place as
            the Board may designate. Meetings of the Board may be held either in
            person or by means of telephonic, video conference or other
            communications facility that permits all participants to communicate
            adequately with each other during the meeting, at the offices of the
            Corporation or such other place (either within or outside Canada) as
            may be determined from time to time by the Board. Written notice of
            each meeting of the Board shall be given to each Director at least
            two clear days before each such meeting.
          </li>
          <li>
            Quorum. The presence of a at least {directorData[0].quorum} Director
            then in office shall constitute a quorum. If a quorum is not
            achieved at any duly called meeting, such meeting may be postponed
            to a time no earlier than 72 hours after written notice of such
            postponement has been given to the Directors.
          </li>
        </ol>
        <h6>ARTICLE 5: COVENANTS OF THE CORPORATION</h6>
        <h6>5.1 Information Rights</h6>
        <p>
          The Corporation shall prepare and deliver to all the Major Investors
          the following information in the below manner:
        </p>
        <ol type="a">
          <li>
            At the end of every financial year, the Corporation shall deliver,
            as soon as available, unaudited financial statements including
            consolidated balance sheets of the Corporation and, if applicable,
            its subsidiaries as at the end of the financial year as well as
            consolidated statements of income, retained earnings and changes in
            cash flow of the Corporation and its subsidiaries, if applicable,
            for such year, all in accordance with generally accepted accounting
            principles in Canada;
          </li>
          <li>
            For every financial quarter, the management of the Corporation shall
            prepare and deliver a report regarding such quarter’s financial
            results and operations;
          </li>
          <li>
            Before the start of each financial year, the Corporation shall
            outline and deliver its proposed annual business plan as well as an
            operating and capital expenditure budget, approved by the Board;
          </li>
          <li>
            Other information responding to reasonable requests from Investors
            from time to time;
          </li>
        </ol>
        <h6>5.2 Other Covenats</h6>
        <p>
          Without limiting the validity of other covenants and provisions in
          Article 4, and unless waived by holders of Preferred Majority, the
          Corporation further agrees and covenants to abide by the following,
          and to have it subsidiaries, if any, do the same:
        </p>
        <ol type="a">
          <li>
            Financing. The Corporation shall inform the Board of all
            negotiations, offers or contracts relating to possible future
            financing for the Corporation, however this shall not be required
            where such financing is with trade creditors or if such financing
            relates to the Corporation’s use of commercial lending agreements
            with financial institutions.
          </li>
          <li>
            Proprietary Information and Inventions Agreements. From each current
            or future technical or managerial employee, contractor, or
            consultant working for the Corporation, the Corporation shall obtain
            a duly executed agreement relating to proprietary information and
            the assignment of inventions on terms that are acceptable to the
            Board.
          </li>
          <li>
            Confidentiality and Non-Compete Agreements. The Corporation shall
            have all key employees, and as necessary, contractors and
            consultants, enter into confidentiality agreements, non-competition
            agreements, and non-solicitation agreements that are acceptable to
            the Board.
          </li>
        </ol>
      </div>
    );
  }


  const Article6 = function ()
  {
    const certificate = `“The shares represented by this certificate are subject to
    all the terms and conditions of a Shareholders Agreement made as of ${createdDate}
    as it may be amended from time to time, which agreement contains,
    among other things, restrictions on the right of the holder hereof to transfer or sell the shares.
    A copy of such agreement is on file at the registered office of the Corporation.”`;
    return (
      <div>
        <h6>ARTICLE 6: SHARE RIGHTS</h6>
        <h6>6.1 Restrictions</h6>
        <ol>
          <li>
            Shareholders shall not Transfer any Shares to another Person or
            entity, except as expressly authorized by this Agreement. If Shares
            are transferred in contravention of this Agreement, the Corporation
            shall not Transfer on its ledger such shares or treat the owners of
            the Shares as valid Shareholders or otherwise give such owners any
            of the rights of the Shares that they have been transferred to in
            contravention of this Agreement.
          </li>
          <li>
            The following conditions apply to every Transfer of Shares by a
            Shareholder:
            <ol>
              <li>
                The proposed transferee must be legally bound in writing to
                become a party and be bound by this Agreement (if not already
                legally bound) by signing an acknowledgement substantially in
                the form displayed in Schedule “B”, annexed hereto.
              </li>
              <li>
                Such Transfer must be approved in accordance with the Articles
                and any transfer that follows the requirements of Article 6
                shall be deemed to have been consented to by the Shareholders
                for the purposes of any restrictions on Transfer in the
                Articles; and,
              </li>
              <li>
                Such Shareholder shall provide to the Corporation with written
                assurances, in a form satisfactory to the Corporation, that the
                proposed transfer is exempt from registration and prospectus
                requirements of applicable securities laws, and that all
                appropriate action has been taken to comply with such laws.
              </li>
            </ol>
          </li>
        </ol>
        <h6>6.2 Endorsement on Certificates</h6>
        <p>
          Share certificates of the Corporation shall bear the following
          language in addition to such legends as may be required by applicable
          securities laws:{" "}
        </p>
        <p>{certificate}</p>
        <h6>6.3 Pre-Emptive Rights</h6>
        <p>
          The following provisions shall apply to the allotment and issue by the
          Corporation of any shares in its capital stock (“New Shares”):
        </p>
        <h6>6.4 Permitted Transfers</h6>
        <ol type="A">
          <li>
            Each Investor may, after giving notice to the Corporation, transfer
            all or any part of its Shares beneficially owned by it to:
          </li>
          <ol type="a">
            <li>Any Affiliate of such Investor;</li>
            <li>
              Any fund under common management or Control with such Investor (or
              its successor by amalgamation), or whose manager or general
              partner, as applicable, is the same as or an Affiliate of the
              manager or general partner of the Investor (or its successor by
              amalgamation);
            </li>
            <li>
              Any shareholder(s), partner(s), member or investor(s) of such
              Investors where such Transfer is made in connection with a
              distribution of assets to any shareholder(s), partner(s),
              member(s), or investor(s) of the Investor;
            </li>
            <li>
              Any Person in connection with the sale of all or substantially all
              of the assets of such Investor or the liquidation or dissolution
              of such Investor; or
            </li>
            <li>
              Any financial institution that carries on the business of
              providing equity financing as part of a sale of a portfolio of
              equity interests by such Investor;
            </li>
            <li>
              So long as each transferee agrees to be bound by this Agreement
              and that no transferee or its Affiliates is a Competitor of the
              Corporation.
            </li>
          </ol>
          <li>
          Each Shareholder may, after giving notice to the Corporation, Transfer
          all or any part of its Shares beneficially owned by it to:
          </li>
          <ol type="i">
            <li>
              A corporation wholly owned by such Shareholder and/or their Family
              Members; or
            </li>
            <li>
              A custodian, trustee (including an RRSP, RIF, IRA, or similar
              retirement or investment fund) or other fiduciary for such
              Shareholder and/or their Family Members; or
            </li>
          </ol>
          <li>
            Provided that in the case of the Section 6.4 (B), the transferee
            agrees to be bound by this Agreement. In such event:
          </li>
          <ol type="a">
            <li>
              The transferring Shareholder shall remain a party to this
              Agreement;
            </li>
            <li>
              The transferring Shareholder shall take such actions as may be
              necessary to cause the transferee to at all times fully and
              faithfully discharge its obligations pursuant to this Agreement,
              and shall comply with its terms;
            </li>
            <li>
              The transferring Shareholder shall at all times after such
              Transfer be jointly and severally liable with the transferee for
              the discharge of the transferee’s obligations under this Agreement
              and compliance by the transferee with the terms and conditions of
              this Agreement; and,
            </li>
            <li>
              If the transferee is no longer a corporation wholly-owned by the
              transferring Shareholder and/or his respective Family Members or a
              custodian, trustee, or other fiduciary for such Shareholder and/or
              his Family Members, the transferee will immediately transfer all
              of the transferred Shares back to the Transferor.
            </li>
          </ol>
        </ol>
      </div>
    );
  }
  



  
  return (
    <div>
      <Paper className={classes.paperStyle}>
        <Beginning />
        <Recitals />
        <Articles />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article2 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article4 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article6 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article7 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article8 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article9 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article10 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article10b
          corpName={corpName}
          corpContact={corpContact}
          corpFax={corpFax}
        />
      </Paper>
    </div>
  );
};


function Header ({ title, classes })
{

  const handleClick = (e) => {
    e.preventDefault();
  }
  return (
    <a href="#" onClick={(e) => handleClick(e)}>
      <h6>{title}</h6>
    </a>
  );
}
